import React, { createContext, useContext, useState, useEffect } from "react";
import { io } from "socket.io-client";

// Create a Context to hold the socket instance
const SocketContext = createContext();

// Create a custom hook to use the socket context
export const useSocket = () => {
  const socket = useContext(SocketContext); // Access socket from context
  if (!socket) {
    throw new Error("useSocket must be used within a SocketProvider");
  }
  return socket; // Return the socket instance
};

// Create the SocketProvider to manage the socket connection
export const SocketProvider = ({ children }) => {
  const [socket, setSocket] = useState(null); // Socket state to store the socket connection

  useEffect(() => {
    const newSocket = io("http://localhost:5001"); // Connect to backend Socket.IO server
    setSocket(newSocket); // Store socket instance

    return () => newSocket.close(); // Clean up when component unmounts
  }, []);

  useEffect(() => {
    const newSocket = io("http://localhost:5001");
    newSocket.on("connect", () => {
      console.log("✅ Connected to socket server:", newSocket.id);
    });
  
    setSocket(newSocket);
  
    return () => newSocket.close();
  }, []);
  
  return (
    <SocketContext.Provider value={socket}> {/* Provide socket to child components */}
      {children}
    </SocketContext.Provider>
  );
};
