import React, { useState } from "react";
import { useNavigate } from "react-router-dom";  // Import navigation hook

const Home = () => {
  const [username, setUsername] = useState(""); // State to store the name
  const navigate = useNavigate(); // Hook for navigation

  // Function to handle Join Chat
  const handleJoin = () => {
    if (username.trim()) { // Prevent empty names
      navigate(`/chat?name=${username}`); // Redirect to Chat page with username
    }
  };

  return (
    <div className="d-flex flex-column justify-content-center align-items-center vh-100">
      <h2>Welcome to Real-Time Chat</h2>
      <input
        type="text"
        placeholder="Enter your name"
        value={username}
        onChange={(e) => setUsername(e.target.value)} // Update state on input change
      />
      <button onClick={handleJoin}>Join Chat</button>
    </div>
  );
};
export default Home;
