import React, { useEffect, useState } from "react";
 // Import the custom hook for socket connection
import { useSocket } from "../context/SocketContext"; // Correct import path to SocketContext.js


const Chat = () => {
  const socket = useSocket(); // Use the custom hook to get the socket instance
  const [username, setUsername] = useState(""); // Store username in state
  const [message, setMessage] = useState(""); // Store message input
  const [messages, setMessages] = useState([]); // Store chat messages

  // Get the username from localStorage when component mounts
  useEffect(() => {
    const storedUsername = localStorage.getItem("username"); // Retrieve username from localStorage
    if (storedUsername) {
      setUsername(storedUsername); // Set the username in state
    }

    // Retrieve messages from localStorage
    const storedMessages = JSON.parse(localStorage.getItem("messages")) || [];
    setMessages(storedMessages); // Set the chat messages from localStorage

  }, []);

  // Handle sending a message
  const sendMessage = () => {
    if (newMessage.trim() !== "") {
      socket.emit("send_message", {
        content: newMessage,
        sender: user.name,
        time: new Date(),
      });
      setNewMessage(""); // Clear input
    }
  };
  

  // Listen for incoming messages
  useEffect(() => {
    if (!socket) return;
  
    const handleMessage = (data) => {
      setMessages((prev) => {
        const updatedMessages = [...prev, data];
        localStorage.setItem("messages", JSON.stringify(updatedMessages));
        return updatedMessages;
      });
    };
  
    socket.on("receive_message", handleMessage);
  
    return () => {
      socket.off("receive_message", handleMessage);
    };
  }, [socket]);
   // Dependencies updated when socket or messages change

  return (
    <div className="chat-container d-flex flex-column justify-content-center align-items-center vh-100">
      <h2>Welcome to the Chat Room</h2>
      <p>Welcome, <strong>{username}</strong>! Start chatting below.</p>

      <div className="messages-box">
        {messages.map((msg, index) => (
          <div key={index} className="message">
            <strong>{msg.username}: </strong>{msg.message}
          </div>
        ))}
      </div>

      <div className="message-input">
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)} // Update message input state
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chat;
