import React, { useEffect, useState } from "react"; 
import { useSocket } from "../context/SocketContext";

const Chat = () => {
  const socket = useSocket();
  const [username, setUsername] = useState("");
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  // Load username and messages from localStorage
  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setUsername(storedUsername);
    }

    const storedMessages = JSON.parse(localStorage.getItem("messages")) || [];
    setMessages(storedMessages);
  }, []);

  // Send a new message
  const sendMessage = () => {
    if (message.trim() !== "") {
      const newMsg = {
        content: message,
        sender: username,
        time: new Date(),
      };

      socket.emit("send_message", newMsg);
      setMessage(""); // Clear input
    }
  };

  // Listen for incoming messages
  useEffect(() => {
    if (!socket) return;

    const handleMessage = (data) => {
      setMessages((prev) => {
        const updated = [...prev, data];
        localStorage.setItem("messages", JSON.stringify(updated));
        return updated;
      });
    };

    socket.on("receive_message", handleMessage);

    return () => {
      socket.off("receive_message", handleMessage);
    };
  }, [socket]);

  return (
    <div className="chat-container d-flex flex-column justify-content-center align-items-center vh-100 bg-light p-4">
      <h2 className="text-center mb-4">Welcome to the Chat Room</h2>
      <p className="text-center mb-4">Welcome, <strong>{username || "Guest"}</strong>! Start chatting below.</p>

      <div
        className="messages-box bg-white p-3 rounded shadow-sm mb-3"
        style={{ width: "80%", maxHeight: "300px", overflowY: "auto" }}
      >
        {messages.length === 0 ? (
          <div className="text-center text-muted">No messages yet.</div>
        ) : (
          messages.map((msg, index) => (
            <div
              key={index}
              className={`message p-2 mb-2 rounded ${
                msg.sender === username ? "bg-primary text-white" : "bg-light"
              }`}
              style={{ maxWidth: "75%" }}
            >
              <strong>{msg.sender}: </strong>{msg.content}
            </div>
          ))
        )}
      </div>

      <div className="message-input d-flex justify-content-between align-items-center w-75">
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="form-control mr-2"
          style={{ borderRadius: "20px", padding: "10px" }}
        />
        <button
          onClick={sendMessage}
          className="btn btn-primary"
          style={{ borderRadius: "20px", padding: "10px 20px" }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default Chat;
