import React from "react";
import { useLocation } from "react-router-dom";

const Chat = () => {
  const location = useLocation(); // Get current URL data
  const params = new URLSearchParams(location.search); // Extract query params
  const username = params.get("name"); // Get "name" from the URL

  return (
    <div className="d-flex flex-column justify-content-center align-items-center vh-100">
      <h2>Chat Room</h2>
      <p>Welcome, <strong>{username}</strong>! Start chatting below.</p>
    </div>
  );
};

export default Chat;
